import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Modal } from 'react-bootstrap';

const Menu = () => {
    const [entrada, setEntrada] = useState([]);
    const [sobremesas, setSobremesas] = useState([]);
    const [especiais, setEspeciais] = useState([]);
    const [bebidasGeladas, setBebidasGeladas] = useState([]);
    const [chas, setChas] = useState([]);
    const [produtoId, setProdutoId] = useState(null);
    const [quantidade, setQuantidade] = useState(1);
    const [order, setOrder] = useState({ name: '', tableNumber: '', items: [] });
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:3000/api/produtos')
            .then(response => {
                const categorias = {
                    entrada: [],
                    sobremesa: [],
                    bebida: [],
                    prato_principal: [],
                    moda_da_casa: []
                };

                response.data.forEach(item => {
                    if (categorias[item.categoria]) {
                        categorias[item.categoria].push({
                            id: item.id,
                            nome: item.nome_produto,
                            preco: item.preco
                        });
                    }
                });

                setEntrada(categorias.entrada);
                setSobremesas(categorias.sobremesa);
                setEspeciais(categorias.bebida);
                setBebidasGeladas(categorias.prato_principal);
                setChas(categorias.moda_da_casa);
            })
            .catch(error => console.error('Erro ao buscar os produtos:', error));
    }, []);

    const handleAddItem = () => {
        const produto = [...entrada, ...sobremesas, ...especiais, ...bebidasGeladas, ...chas].find(p => p.id === parseInt(produtoId));
        if (produto) {
            setOrder(prevOrder => ({
                ...prevOrder,
                items: [...prevOrder.items, { ...produto, quantidade }]
            }));
            setShowModal(false);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.post('http://localhost:3000/api/pedido', {
            nome: order.name,
            mesa: order.tableNumber,
            items: order.items,
        })
        .then(response => {
            alert(`Pedido enviado com sucesso! Valor total: R$ ${response.data.totalPrice.toFixed(2)}`);
            setOrder({ name: '', tableNumber: '', items: [] });
        })
        .catch(error => {
            console.error('Erro ao enviar o pedido:', error);
            alert('Erro ao enviar o pedido.');
        });
    };

    const calcularTotal = () => {
        return order.items.reduce((total, item) => total + item.preco * item.quantidade, 0);
    };

    return (
        <section className='secao_pedidos'>
            <div className='body'>
                <h1>Selecione o Produto e a Quantidade</h1>
                <form onSubmit={handleSubmit} className='form'>
                    <div className='box_bados_user'>
                        <input 
                            type="text" 
                            value={order.name} 
                            onChange={(e) => setOrder({ ...order, name: e.target.value })} 
                            placeholder='Nome do cliente' />

                        <input 
                            type="text" 
                            value={order.tableNumber} 
                            onChange={(e) => setOrder({ ...order, tableNumber: e.target.value })} 
                            placeholder='Mesa do cliente' />
                    </div>
                    <div className='box_Categorias'>
                        <Button className='button_pedidos' onClick={() => setShowModal(true)}>Adicionar Produto</Button>
                    </div>
                    <h2>Total: R${calcularTotal().toFixed(2)}</h2>
                    <button type="submit">Enviar pedido</button>
                </form>
                <div className='Notinha'>
                    <h2>Itens do Pedido:</h2>
                    <ul>
                        {order.items.map((item, index) => (
                            <li key={index}>
                                {item.nome} - {item.quantidade}x R${item.preco.toFixed(2)} = R${(item.preco * item.quantidade).toFixed(2)}
                            </li>
                        ))}
                    </ul>
                </div>
                <Modal show={showModal} onHide={() => setShowModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Escolha seu produto</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <select value={produtoId} onChange={(e) => setProdutoId(e.target.value)}>
                            {[...entrada, ...sobremesas, ...especiais, ...bebidasGeladas, ...chas].map(produto => (
                                <option key={produto.id} value={produto.id}>{produto.nome} - R$ {produto.preco.toFixed(2)}</option>
                            ))}
                        </select>
                        <input type='number' min='1' value={quantidade} onChange={(e) => setQuantidade(Number(e.target.value))} />
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant='primary' onClick={handleAddItem}>Adicionar</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        </section>    
    );
};

export default Menu;
