    sobremesa:[],
  });

  useEffect (()=>{
    axios.get('http://localhost:3000/api/produtos')
    .then(sobr =>{
      const principais = ({
        prato_principal:[]
      });

      princ.data.forEach(valor=>{
        if (!principais[valor.categoria]) {
          principais[valor.categoria] = [];
        }
        principais[valor.categoria].push(valor);
      })

      setMenuPrincipal(principais);
