// src/ProductForm.js
import React, { useState,useEffect } from 'react';
import axios from 'axios';
import {Modal, Button} from 'react-bootstrap';
import './Pedidos.css';

const ProductForm =() => {
    //useState do usuario---------------------------
    const [order, setOrder] = useState({
        name: '',
        tambleNumber: '',
        items: []
    })

   //Get no banco de daods dos produtos e suas respectivas categorias------
   useEffect(()=>{
    axios.get('http://localhost:3000/api/produtos')
    .then(response=>{
        //listas para receber as categorias certas
        const categoriesEntrada ={
            entrada:{}
        }
        const categoriesSobremesa = {
            sobremesa:{}
        }
        const categoriesBebidas = {
            bebida:{}
        }
        const categoriesPrincipal = {
            prato_principal:{}
        }
        const categoriesCasa = {
            moda_da_casa:{}
        }
    //desembaralhador de categorias-------    
    response.data.forEach(item=>{
        if(categoriesEntrada[item.categoria]){
            categoriesEntrada[item.categoria][item.nome_produto] = item.preco;
        } 
        else if(categoriesSobremesa[item.categoria]){
            categoriesSobremesa[item.categoria][item.nome_produto] = item.preco;
        }
        else if(categoriesBebidas[item.categoria]){
            categoriesBebidas[item.categoria][item.nome_produto] = item.preco;
        }
        else if(categoriesPrincipal[item.categoria]){
            categoriesPrincipal[item.categoria][item.nome_produto] = item.preco;
        } 
        else{
            categoriesCasa[item.categoria][item.nome_produto] = item.preco
        };
    });
    setEntradaId(categoriesEntrada);
    setSobremesaId(categoriesSobremesa);
    setBebidaId(categoriesBebidas);
    setPrincipalId(categoriesPrincipal);
    setModaCasaId(categoriesCasa);
    })
})

    //UseStates dos dados do cliente------------------------------
    const [name, setName] = useState('');
    const [tableNumber, setTableNumber] = useState('');

    //UseStates das listas acima----------------------------------
    const [entradaId, setEntradaId] = useState([]);
    const [principalId, setPrincipalId] = useState([]);
    const [modaCasaId, setModaCasaId] = useState([]);
    const [sobremesaId, setSobremesaId] = useState([]);
    const [bebidaId, setBebidaId] = useState([]);

    //setShows de cada opção----------------------------------------
    const [entradaModal, setEntradaModal] = useState(false);
    const [principalModal, setPrincipalModal] = useState(false);
    const [modaCasaModal, setModaCasaModal] = useState(false);
    const [sobremesaModal, setSobremesaModal] = useState(false);
    const [bebidaModal, setBebidaModal] = useState(false);

    const [quantidade, setQuantidade] = useState(1);

    //Atualizações dos produtos---------------------------- 
    const handleEntradaChange = (e) => {
        setEntradaId(Number(e.target.value));
    };
    const handlePrincipalChange = (e) => {
        setPrincipalId(Number(e.target.value));
    };
    const handleModaCasaChange = (e) => {
        setModaCasaId(Number(e.target.value));
    };
    const handleSobremesaChange = (e) => {
        setSobremesaId(Number(e.target.value));
    };
    const handleBebidaChange = (e) => {
        setBebidaId(Number(e.target.value));
    };

    const handleQuantidadeChange = (e) => {
        setQuantidade(Number(e.target.value));
    };

    //botões do form para pop-ups------------------------------------------
    const handleEntradaClick = () => {
        setQuantidade(1);
        setEntradaModal(true);
    };
    const handlePrincipalClick = () => {
        setQuantidade(1);
        setPrincipalModal(true);
    };
    const handleModaCasaClick = () => {
        setQuantidade(1);
        setModaCasaModal(true);
    };
    const handleSobremesaClick = () => {
        setQuantidade(1);
        setSobremesaModal(true);
    };
    const handleBebidaClick = () => {
        setQuantidade(1);
        setBebidaModal(true);
    };

    //calculo total-------------------------------
    const CalculoTotal =() =>{
        return order.items.reduce((Total, valor) => Total + valor.preco * valor.quantidade, 0)
    };

    const handleSubmit = (e) =>{
        e.preventDefault();
        const totalPedido = order.items.reduce((acc, item) => acc + item.preco * item.quantidade, 0);
        alert(`Pedido enviado com sucesso! Valor total: R$ ${totalPedido.toFixed(2)}`);
        setOrder({
            name: '',
            tableNumber: '',
            items: []
        });
    };

    return (
        <section className='secao_pedidos '>
            <div className='body'>
                <h1>Selecione o Produto e a Quantidade</h1>

                <form onSubmit={handleSubmit} className='form'>
                <div className='box_bados_user'>
                    <input 
                        type="text" 
                        value={name} 
                        onChange={(e) => setName(e.target.value)} 
                        placeholder='Nome do cliente'/>

                    <input 
                        type="text" 
                        value={tableNumber} 
                        onChange={(e) => setTableNumber(e.target.value)} 
                        placeholder='Mesa do cliente'/>
                </div>

                    <div className='box_Categorias'>
                        <Button className='button_pedidos' onClick={handleEntradaClick}>Entradas</Button>
                        <Button className='button_pedidos' onClick={handlePrincipalClick}>Pratos Principais</Button>
                        <Button className='button_pedidos' onClick={handleModaCasaClick}>A Moda da Casa</Button>
                        <Button className='button_pedidos' onClick={handleSobremesaClick}>Sobremesas</Button>
                        <Button className='button_pedidos' onClick={handleBebidaClick}>Bebidas</Button>
                    </div>
                    <h2>Total: R${CalculoTotal().toFixed(2)}</h2>
                    <button type="submit">Enviar pedido</button>
                </form>

                <div className='Notinha'>
                    <h2>Itens do Pedido:</h2>
                    <ul>
                        {order.items.map((item, index) => (
                            <li key={index}>
                                {item.nome} - {item.quantidade}x R${item.preco.toFixed(2)} = R${(item.preco * item.quantidade).toFixed(2)}
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Modal Entradas */}
                <Modal show={entradaModal} onHide={() => setEntradaModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Selecione a entrada e a quantidade</Modal.Title>
                        <Modal.Body>
                            <div className='Box_produto'>
                                <label htmlFor='entradaSelect'>Entrada:</label>
                                <select className='produtoSelect' name='entradaSelect' value={entradaId} onChange={handleEntradaChange}>
                                    {Object.keys(entradaId).map(categoria => (
                                        <option key={categoria.id} value={categoria.id}> 
                                            {categoria} - R${categoria.preco.toFixed(2)}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='Box_quantidade'>
                                <label> Quantidade:</label>
                                <input type="number" value={quantidade} onChange={handleQuantidadeChange} min="1"/>
                            </div>
                            <Modal.Footer>
                                <Button className='btn-secondary' variant='secondary' onClick={()=> setEntradaModal(false)}>
                                    Cancelar
                                </Button>
                                <Button className='btn-primary' variant='primary' onClick={() => handleAddItem("Entradas", entradaId, quantidade)}>
                                    Adicionar
                                </Button>
                            </Modal.Footer>
                        </Modal.Body>
                    </Modal.Header>
                </Modal>

                {/* Modal Pratos Principais */}
                <Modal show={principalModal} onHide={() => setPrincipalModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Selecione o prato principal e a quantidade</Modal.Title>
                        <Modal.Body>
                            <div className='Box_produto'>
                                <label htmlFor='principalSelect'>Prato Principal:</label>
                                <select className='produtoSelect' name='principalSelect' value={principalId} onChange={handlePrincipalChange}>
                                    {principais.map(principal => (
                                        <option key={principal.id} value={principal.id}> 
                                            {principal.nome} - R${principal.preco.toFixed(2)}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='Box_quantidade'>
                                <label> Quantidade:</label>
                                <input type="number" value={quantidade} onChange={handleQuantidadeChange} min="1"/>
                            </div>
                            <Modal.Footer>
                                <Button className='btn-secondary' variant='secondary' onClick={()=> setPrincipalModal(false)}>
                                    Cancelar
                                </Button>
                                <Button className='btn-primary' variant='primary' onClick={() => handleAddItem("Principais", principalId, quantidade)}>
                                    Adicionar
                                </Button>
                            </Modal.Footer>
                        </Modal.Body>
                    </Modal.Header>
                </Modal>

                {/* Modal A Moda da Casa */}
                <Modal show={modaCasaModal} onHide={() => setModaCasaModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Selecione o prato especial e a quantidade</Modal.Title>
                        <Modal.Body>
                            <div className='Box_produto'>
                                <label htmlFor='modaCasaSelect'>A Moda da Casa:</label>
                                <select className='produtoSelect' name='modaCasaSelect' value={modaCasaId} onChange={handleModaCasaChange}>
                                    {modaCasa.map(prato => (
                                        <option key={prato.id} value={prato.id}> 
                                            {prato.nome} - R${prato.preco.toFixed(2)}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='Box_quantidade'>
                                <label> Quantidade:</label>
                                <input type="number" value={quantidade} onChange={handleQuantidadeChange} min="1"/>
                            </div>
                            <Modal.Footer>
                                <Button className='btn-secondary' variant='secondary' onClick={()=> setModaCasaModal(false)}>
                                    Cancelar
                                </Button>
                                <Button className='btn-primary' variant='primary' onClick={() => handleAddItem("Moda da Casa", modaCasaId, quantidade)}>
                                    Adicionar
                                </Button>
                            </Modal.Footer>
                        </Modal.Body>
                    </Modal.Header>
                </Modal>

                {/* Modal Sobremesas */}
                <Modal show={sobremesaModal} onHide={() => setSobremesaModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Selecione a sobremesa e a quantidade</Modal.Title>
                        <Modal.Body>
                            <div className='Box_produto'>
                                <label htmlFor='sobremesaSelect'>Sobremesa:</label>
                                <select className='produtoSelect' name='sobremesaSelect' value={sobremesaId} onChange={handleSobremesaChange}>
                                    {sobremesas.map(sobremesa => (
                                        <option key={sobremesa.id} value={sobremesa.id}> 
                                            {sobremesa.nome} - R${sobremesa.preco.toFixed(2)}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='Box_quantidade'>
                                <label> Quantidade:</label>
                                <input type="number" value={quantidade} onChange={handleQuantidadeChange} min="1"/>
                            </div>
                            <Modal.Footer>
                                <Button className='btn-secondary' variant='secondary' onClick={()=> setSobremesaModal(false)}>
                                    Cancelar
                                </Button>
                                <Button className='btn-primary' variant='primary' onClick={() => handleAddItem("Sobremesas", sobremesaId, quantidade)}>
                                    Adicionar
                                </Button>
                            </Modal.Footer>
                        </Modal.Body>
                    </Modal.Header>
                </Modal>

                {/* Modal Bebidas */}
                <Modal show={bebidaModal} onHide={() => setBebidaModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Selecione a bebida e a quantidade</Modal.Title>
                        <Modal.Body>
                            <div className='Box_produto'>
                                <label htmlFor='bebidaSelect'>Bebida:</label>
                                <select className='produtoSelect' name='bebidaSelect' value={bebidaId} onChange={handleBebidaChange}>
                                    {bebidas.map(bebida => (
                                        <option key={bebida.id} value={bebida.id}> 
                                            {bebida.nome} - R${bebida.preco.toFixed(2)}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='Box_quantidade'>
                                <label> Quantidade:</label>
                                <input type="number" value={quantidade} onChange={handleQuantidadeChange} min="1"/>
                            </div>
                            <Modal.Footer>
                                <Button className='btn-secondary' variant='secondary' onClick={()=> setBebidaModal(false)}>
                                    Cancelar
                                </Button>
                                <Button className='btn-primary' variant='primary' onClick={() => handleAddItem("Bebidas", bebidaId, quantidade)}>
                                    Adicionar
                                </Button>
                            </Modal.Footer>
                        </Modal.Body>
                    </Modal.Header>
                </Modal>
            </div>
        </section>
    );
}

export default ProductForm;
