// src/ProductForm.js
import React, { useState,useEffect } from 'react';
import axios from 'axios';
import {Modal, Button} from 'react-bootstrap';
import './Pedidos.css';

const ProductForm =() => {
    //useState do usuario---------------------------
    const [order, setOrder] = useState({
        name: '',
        tambleNumber: '',
        items: []
    })

   //Get no banco de daods dos produtos e suas respectivas categorias------
   useEffect(() => {
    axios.get('http://localhost:3000/api/produtos')
        .then(response => {
            const categorias = {
                entrada: {},
                sobremesa: {},
                bebida: {},
                prato_principal: {},
                moda_da_casa: {}
            };

            response.data.forEach(item => {
                if (categorias[item.categoria]) {
                    categorias[item.categoria].push(item);
                }
            });

            setUseProdutos(categorias);
        })
        .catch(error => console.error("Erro ao buscar produtos:", error));
    }, []);

    //UseStates das listas acima----------------------------------
    const [useProdutos, setUseProdutos] = useState({});
    //setShows de cada opção----------------------------------------
    const [ProdutosModal, setProdutosModal] = useState(false);

    const [quantidade, setQuantidade] = useState(1);

    const [categoriaAtual, setCategoriaAtual] = useState('');
    const [itemAtual, setItematual] = useState('');


    //botões do form para pop-ups------------------------------------------
    const handleEntradaClick = (categoria) => {
        setCategoriaAtual(categoria);
        setItematual('');
        setQuantidade(1);
        setProdutosModal(true);
    };


    const handleAddItem = () => {
        if(!itemAtual || categoriaAtual<=0){
            alert('Você precisa adicionar um produto e uma quantidade para effetuar a compra');
            return;
        }

        const atualizarItens = [...order.items];
        const existingItem = renovarItens.items.findIndex(item=> item.categoria === categoriaAtual && item.nome === itemAtual);

        if (existingItem) {
            atualizarItens[existingItem].quantidade += quantidade;
        } else {
            atualizarItens.push({nome:itemAtual, quantidade:quantidade, categoria: categoriaAtual});
        }

        setOrder({ 
            ...prevOrder, 
            items: newItems 
        }); 

        setProdutosModal(false);
    }

    const handleSubmit = (e) =>{
        e.preventDefault();

        axios.post('http://localhost:3000/api/pedidos',{
            nome: order.name,
            mesa: order.tambleNumber,
            items: order.items
        })
        .then(result =>{
            alert(`Pedido enviado com sucesso! Valor total: R$ ${result.data.total.toFixed(2)}`);
            setOrder({
                name: '',
                tableNumber: '',
                items: []
            });
        }) 
        .catch(error =>{
            console.log('Ocorreu um erro no envio do pedido!', error);
            alert('Erro ao efetuar a compra!!');
        });


    };

    return (
        <section className='secao_pedidos '>
            <div className='body'>
                <h1>Selecione o Produto e a Quantidade</h1>

                <form onSubmit={handleSubmit} className='form'>
                <div className='box_bados_user'>
                    <input 
                        type="text" 
                        value={order.name} 
                        onChange={(e) => setOrder({...order,name: e.target.value})} 
                        placeholder='Nome do cliente'/>

                    <input 
                        type="text" 
                        value={order.tambleNumber} 
                        onChange={(e) => setOrder({...order,tableNumber: e.target.value})} 
                        placeholder='Mesa do cliente'/>
                </div>

                    <div className='box_Categorias'>
                        {Object.key}
                        <Button className='button_pedidos' onClick={handleEntradaClick}>Entradas</Button>
                    </div>
                    <h2>Total: R${CalculoTotal().toFixed(2)}</h2>
                    <button type="submit">Enviar pedido</button>
                </form>

                <div className='Notinha'>
                    <h2>Itens do Pedido:</h2>
                    <ul>
                        {order.items.map((item, index) => (
                            <li key={index}>
                                {item.nome} - {item.quantidade}x R${item.preco.toFixed(2)} = R${(item.preco * item.quantidade).toFixed(2)}
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Modal Entradas */}
                <Modal show={entradaModal} onHide={() => setEntradaModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Selecione a entrada e a quantidade</Modal.Title>
                        <Modal.Body>
                            <div className='Box_produto'>
                                <label htmlFor='entradaSelect'>Entrada:</label>
                                <select className='produtoSelect' name='entradaSelect' value={entradaId} onChange={handleEntradaChange}>
                                    {Object.keys(entradaId).map(categoria => (
                                        <option key={categoria} value={categoria}> 
                                            {categoria} - R${entradaId.preco.toFixed(2)}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='Box_quantidade'>
                                <label> Quantidade:</label>
                                <input type="number" value={quantidade} onChange={handleQuantidadeChange} min="1"/>
                            </div>
                            <Modal.Footer>
                                <Button className='btn-secondary' variant='secondary' onClick={()=> setEntradaModal(false)}>
                                    Cancelar
                                </Button>
                                <Button className='btn-primary' variant='primary' onClick={() => handleAddItem("Entradas", entradaId, quantidade)}>
                                    Adicionar
                                </Button>
                            </Modal.Footer>
                        </Modal.Body>
                    </Modal.Header>
                </Modal>

            </div>
        </section>
    );
}

export default ProductForm;
