import React from 'react';
import './componentes.css';

const Chas = () => {
  const [menuPrincipal, setMenuPrincipal] = useState({
    prato_principal:[],
  });

  useEffect (()=>{
    axios.get('http://localhost:3000/api/produtos')
    .then(princ =>{
      const principais = ({
        prato_principal:[]
      });

      princ.data.forEach(valor=>{
        if (!principais[valor.categoria]) {
          principais[valor.categoria] = [];
        }
        principais[valor.categoria].push(valor);
      })

      setMenuPrincipal(principais);
    })
    .catch(error =>{
      console.log('Erro ao buscar pratos de entrada', error);
    });

  }, [])

  return (
    <div className="contain">
      <div className='contain-2'>
        <h2 className='categoria'>Chás</h2>
        <ul>
          <li><p>Chá de Hortelã</p>R$ 4.50</li>
          <li><p>Chá Verde</p>R$ 5.00</li>
          <li><p>Chá de Camomila</p>R$ 4.50</li>
          <li><p>Chá de Frutas Vermelhas</p>R$ 6.00</li>
          <li><p>Chá de Gengibre e Limão</p>5.50</li>
        </ul>
      </div>
      <div className='Image1'></div>
    </div>
  );
};

export default Chas;
